function Parallax(){var n=$(window);$('section[data-type="background"]').each(function(){var a=$(this);$(window).scroll(function(){var o=-(n.scrollTop()/a.data("speed")),t="50% "+o+"px";a.css({backgroundPosition:t})})})}function Menu(){$(".menu").on("click",function(){$(this).toggleClass("menu on"),$(this).parent().parent().find(".bar2").slideToggle()})}$(document).ready(function(){Parallax(),Menu()});
